import React, { useState, useEffect, useCallback } from 'react';
import { notionAPI } from '../services/api';
import { NotionPage, PRDDocument, LocalPRD, PageType } from '../types';
import './PRDReviewPage.css';

const PRDReviewPage: React.FC = () => {
  // State management
  const [prdDocuments, setPrdDocuments] = useState<PRDDocument[]>([]);
  const [selectedPRD, setSelectedPRD] = useState<PRDDocument | null>(null);
  const [localPRDs, setLocalPRDs] = useState<LocalPRD[]>([]);
  const [loading, setLoading] = useState(false);
  const [editorContent, setEditorContent] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [showNewPRDModal, setShowNewPRDModal] = useState(false);
  const [newPRDTitle, setNewPRDTitle] = useState('');

  // Load PRDs on component mount
  useEffect(() => {
    loadPRDs();
    loadLocalPRDs();
  }, []);

  // Load PRDs from Notion
  const loadPRDs = async () => {
    setLoading(true);
    try {
      const response = await notionAPI.getPages(PageType.prd, 100, 0);
      const prdDocs: PRDDocument[] = response.data.map((page: NotionPage) => ({
        id: page.id.toString(),
        title: page.title,
        content: page.content || '',
        created_at: page.created_at,
        updated_at: page.updated_at,
        is_local: false,
        notion_url: page.notion_url,
        file_type: 'text' as const,
      }));
      setPrdDocuments(prdDocs);
    } catch (error) {
      console.error('Failed to load PRDs:', error);
    } finally {
      setLoading(false);
    }
  };

  // Load local PRDs from localStorage
  const loadLocalPRDs = () => {
    const stored = localStorage.getItem('local-prds');
    if (stored) {
      const localPRDs = JSON.parse(stored);
      setLocalPRDs(localPRDs);
    }
  };

  // Save local PRDs to localStorage
  const saveLocalPRDs = useCallback((prds: LocalPRD[]) => {
    localStorage.setItem('local-prds', JSON.stringify(prds));
    setLocalPRDs(prds);
  }, []);

  // Create new PRD
  const createNewPRD = () => {
    if (!newPRDTitle.trim()) return;

    const newPRD: LocalPRD = {
      id: `local-${Date.now()}`,
      title: newPRDTitle.trim(),
      content: '# ' + newPRDTitle.trim() + '\n\n## Overview\n\nWrite your PRD content here...\n\n## Features\n\n## User Stories\n\n## Success Metrics\n\n',
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
      is_local: true,
    };

    const updatedLocalPRDs = [...localPRDs, newPRD];
    saveLocalPRDs(updatedLocalPRDs);

    // Convert to PRDDocument and select it
    const prdDoc: PRDDocument = {
      ...newPRD,
      file_type: 'markdown',
    };

    setSelectedPRD(prdDoc);
    setEditorContent(newPRD.content);
    setIsEditing(true);
    setShowNewPRDModal(false);
    setNewPRDTitle('');
  };

  // Select a PRD document
  const selectPRD = async (prd: PRDDocument) => {
    setSelectedPRD(prd);
    setIsEditing(prd.is_local);

    if (prd.is_local) {
      const localPRD = localPRDs.find(p => p.id === prd.id);
      setEditorContent(localPRD?.content || '');
    } else {
      // Load detailed content for Notion PRDs
      try {
        const response = await notionAPI.getPageDetails(parseInt(prd.id));
        setEditorContent(response.data.content || '');
      } catch (error) {
        console.error('Failed to load PRD details:', error);
        setEditorContent(prd.content || '');
      }
    }
  };

  // Save local PRD changes
  const savePRDChanges = () => {
    if (!selectedPRD?.is_local) return;

    const updatedLocalPRDs = localPRDs.map(prd => 
      prd.id === selectedPRD.id 
        ? { ...prd, content: editorContent, updated_at: new Date().toISOString() }
        : prd
    );
    saveLocalPRDs(updatedLocalPRDs);
  };

  // Get all PRDs (both local and Notion)
  const allPRDs = [
    ...localPRDs.map(prd => ({ ...prd, file_type: 'markdown' as const })),
    ...prdDocuments
  ].sort((a, b) => new Date(b.updated_at).getTime() - new Date(a.updated_at).getTime());

  return (
    <div className="prd-review-page">
      {/* Left Sidebar - PRD List */}
      <div className="prd-sidebar">
        <div className="sidebar-header">
          <h3>PRD Documents</h3>
          <button 
            className="new-prd-btn"
            onClick={() => setShowNewPRDModal(true)}
          >
            + New PRD
          </button>
        </div>

        <div className="prd-list">
          {loading && <div className="loading">Loading PRDs...</div>}

          {allPRDs.map((prd) => (
            <div
              key={prd.id}
              className={`prd-item ${selectedPRD?.id === prd.id ? 'selected' : ''}`}
              onClick={() => selectPRD(prd)}
            >
              <div className="prd-item-header">
                <h4>{prd.title}</h4>
                <div className="prd-badges">
                  {prd.is_local && <span className="badge local">Local</span>}
                  {!prd.is_local && <span className="badge notion">Notion</span>}
                </div>
              </div>
              <div className="prd-item-meta">
                <span className="date">
                  {new Date(prd.updated_at).toLocaleDateString()}
                </span>
              </div>
            </div>
          ))}

          {allPRDs.length === 0 && !loading && (
            <div className="empty-state">
              <p>No PRDs found.</p>
              <p>Create a new PRD or import from Notion.</p>
            </div>
          )}
        </div>
      </div>

      {/* Central Editor Area */}
      <div className="prd-editor">
        {selectedPRD ? (
          <>
            <div className="editor-header">
              <h2>{selectedPRD.title}</h2>
              <div className="editor-actions">
                {selectedPRD.is_local && (
                  <>
                    <button
                      className="save-btn"
                      onClick={savePRDChanges}
                      disabled={!isEditing}
                    >
                      Save
                    </button>
                    <button
                      className="edit-btn"
                      onClick={() => setIsEditing(!isEditing)}
                    >
                      {isEditing ? 'Preview' : 'Edit'}
                    </button>
                  </>
                )}
                {selectedPRD.notion_url && (
                  <a
                    href={selectedPRD.notion_url}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="notion-link"
                  >
                    Open in Notion
                  </a>
                )}
              </div>
            </div>

            <div className="editor-content">
              {isEditing && selectedPRD.is_local ? (
                <textarea
                  className="markdown-editor"
                  value={editorContent}
                  onChange={(e) => setEditorContent(e.target.value)}
                  placeholder="Write your PRD content in Markdown..."
                />
              ) : (
                <div className="markdown-preview">
                  <pre>{editorContent}</pre>
                </div>
              )}
            </div>
          </>
        ) : (
          <div className="no-selection">
            <h3>Select a PRD to view or edit</h3>
            <p>Choose a PRD from the sidebar or create a new one to get started.</p>
          </div>
        )}
      </div>

      {/* Right Sidebar - Analysis */}
      <div className="prd-analysis">
        <div className="analysis-header">
          <h3>PRD Analysis</h3>
        </div>
        <div className="analysis-content">
          <div className="analysis-section">
            <h4>Quick Overview</h4>
            <p>AI-powered analysis will appear here</p>
          </div>

          <div className="analysis-section">
            <h4>Key Features</h4>
            <ul>
              <li>Feature analysis coming soon</li>
            </ul>
          </div>

          <div className="analysis-section">
            <h4>User Personas</h4>
            <p>Persona analysis will be shown here</p>
          </div>

          <div className="analysis-section">
            <h4>Success Metrics</h4>
            <p>Metrics analysis coming soon</p>
          </div>

          <div className="analysis-section">
            <h4>Recommendations</h4>
            <p>AI recommendations will appear here</p>
          </div>
        </div>
      </div>

      {/* New PRD Modal */}
      {showNewPRDModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Create New PRD</h3>
              <button
                className="close-btn"
                onClick={() => setShowNewPRDModal(false)}
              >
                Ã—
              </button>
            </div>
            <div className="modal-content">
              <input
                type="text"
                className="prd-title-input"
                placeholder="Enter PRD title..."
                value={newPRDTitle}
                onChange={(e) => setNewPRDTitle(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && createNewPRD()}
                autoFocus
              />
            </div>
            <div className="modal-actions">
              <button
                className="cancel-btn"
                onClick={() => setShowNewPRDModal(false)}
              >
                Cancel
              </button>
              <button
                className="create-btn"
                onClick={createNewPRD}
                disabled={!newPRDTitle.trim()}
              >
                Create PRD
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PRDReviewPage;
